INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1473,'Marshall','LIM','500',3,'Fundamentals of Library and Information Science','An overview of the history of recorded knowledge from ancient times to the digital age informed by the cultural implications of those eras. Open only to Library and Information Science majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1474,'Marshall','LIM','503',3,'Information Description, Organization, and Retrieval','An overview and critical analysis of current practices of information organization, cataloguing, access and reference services, including a conceptual understanding of these skills.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1475,'Marshall','LIM','504',3,'Research Methods in Library and Information Management','An overview of research methods in information management including the conceptualization of research problems, literature reviews, research design, sampling, measurement, data collection and data analysis. Open only to Library and Information Science students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1476,'Marshall','LIM','511',3,'Instructional Strategies for Information Professionals','An overview of research in learning emphasizing the role of academic librarians as instructors and facilitators of information navigation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1477,'Marshall','LIM','562',3,'Library and Information Technology Management','Overview of management of library information technologies including computer networks, design, architecture, cloud technologies, data management, and technology planning as well as emerging technologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1478,'Marshall','LIM','591',2,'Research and Professional Applications','Apply concepts learned during the MMLIS program and investigate research questions and professional problems of concern to employers, their institutions and the profession at large. Open only to: Library and Information Science majors. CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1479,'Marshall','LIM','598',3,'Capstone in Library and Information Management','Student driven research or project-based experience that integrates the knowledge from coursework and applies it to current issues in the field.',1);
