INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1509,'Marshall','MKT','597',0.5,'Consulting Project in Marketing','Individual or team project solving real business problems for an existing business entity, domestic and/or international. Proposal, field research, analyses and oral and written presentations. Graded CR/NC',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1510,'Marshall','MKT','613',3,'Marketing Models in Consumer and Business-to-Business Markets','Modeling research on marketing with a focus on channel structure, franchising and sales force compensation, innovation and diffusion, inter-store competition, sales promotions, and market segmentation. Open to students in the doctoral program in business administration only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1511,'Marshall','MOR','461',4,'Design of Effective Organizations','Designing management systems and practices that lead to organizational excellence; techniques for organizational change.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1512,'Marshall','MOR','462',4,'Management Consulting','Role of professional consultants; data gathering methods; consulting approaches from strategy, finance, operations, information systems, marketing, and human resources; action planning; ethical and career issues.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1513,'Marshall','MOR','469',4,'Negotiation and Persuasion','Theories, strategies, and ethics underlying negotiation and persuasion in contemporary organizations and societies. Emphasizes the knowledge and skills needed for effective negotiation and persuasion.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1514,'Marshall','MOR','470',4,'Global Leadership','Major theories and practices of leading people in multi-national firms. Skills for facilitating cooperation, communication, and motivation among people from different cultures. <strong>Recommended preparation:</strong> BUAD 304',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1515,'Marshall','MOR','471',4,'Managing and Developing People','Management of the human resource function for organizational performance. <strong>Recommended preparation:</strong> BUAD 304.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1516,'Marshall','MOR','472',4,'Power, Politics and Influence','Theories and practices about how power, politics and influence affect organizational life. Knowledge and skills for diagnosing and managing these features of an organization.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1517,'Marshall','MOR','475',1,'The Art of Leadership - Self Discovery','Introductory leadership. Self-examination of personal leadership styles using survey tools, comparative models and in-class exercises. Duplicates credit in former MOR 476a. Open only to students in the USC Warren Bennis Scholars Program.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1518,'Marshall','MOR','477',1,'The Art of Leadership - Change Leadership','Examination of practical challenges facing every leader through a series of in-class discussions and field trips to meet recognized leaders in the social impact sphere. Open only to students in the USC Warren Bennis Scholars Program.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1519,'Marshall','MOR','479',4,'The Business of Sports','Addresses the business side of the sports industry; examines professional sports franchises, amateur athletics, and collegiate sports and how they relate to corporate America, the media, and the public sector. Recommended Preparation BUAD 304.',1);
