INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1621,'Cinematic','CTAN','555',4,'Animation Design and Production','Exploring creative strategies to designing form and content. Developing style and investigating multiple techniques, including live action and sound. Production of a 30-60 second work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1622,'Cinematic','CTAN','563',2,'Advanced Computer Animation','Investigation of advanced computer techniques related to character representation and various types of algorithmically defined animation produced on either film or videotape.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1623,'Cinematic','CTAN','565L',2,'Motion Capture Performance','The art of directing, acting, and creating story for motion capture will be explored while learning the technology behind bringing virtual actors to life.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1624,'Cinematic','CTAN','577a',2,'Fundamentals of Animation','The exploration of the techniques of the art of character animation with an emphasis on discipline, performance and personality observation, specializing in classical Hollywood animation. Open only to Animation and Digital Arts master students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1625,'Cinematic','CTAN','579',2,'Expanded Animation','Incorporation of traditional image making methods as well as digital and new media technologies to convey non-linear narratives over internal and external landscapes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1626,'Cinematic','CTAN','592',2,'Master Class','A special projects course in which students produce a major work through weekly meetings with a master artist/animator. Topics must be approved prior to enrollment. <strong>Recommended preparation:</strong> Previous advanced animation production experience. <strong>Recommended preparation:</strong> Previous advanced animation production experience.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1627,'Cinematic','CTAN','593',2,'Directed Studies in Animation','Individual exploration in the areas of contemporary technology, animation techniques or experimental film through internships, residencies or directed studies. Open only to Animation and Digital Arts master students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1628,'Cinematic','CTAN','594a',2,'Master\'s Thesis','Credit on acceptance of Thesis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1629,'Cinematic','CTAN','599',2,'Special Topics','Detailed investigation of new or emerging aspects of cinema; special subjects offered by visiting faculty; experimental subjects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1630,'Cinematic','CTCS','190g',4,'Introduction to Cinema','Gateway to majors and minors in cinematic arts. Technique, aesthetics, criticism, and social implications of cinema. Lectures accompanied by screenings of appropriate films.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1631,'Cinematic','CTCS','191',4,'Introduction to Television and Video','Exploration of the economic, technological, aesthetic, and ideological characteristics of the televisual medium; study of historical development of television and video including analysis of key works; introduction to TV/Video theory and criticism.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1632,'Cinematic','CTCS','200g',4,'History of the International Cinema I','The development of international cinema from its beginnings to World War II. Lectures, screenings, and discussions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1633,'Cinematic','CTCS','367',4,'Global Media','Studies in the global configurations of television industries and cultures, including new technologies and the textual and sociological analysis of global media events and programming.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1634,'Cinematic','CTCS','393',4,'Postwar Hollywood, 1946-1962','Cinematic and extra-cinematic determinants of Post-Classical and Modernist Hollywood studio and independent genres, styles, and the star-phenomenon and their relationship to American history and culture.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1635,'Cinematic','CTCS','400',4,'Non-Fiction Film and Television','An international survey of documentary, informational, and independent experimental film, video and television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1636,'Cinematic','CTCS','402',4,'Practicum in Film/Television Criticism','Exercise in writing film and television criticism using new and classic films and television programs.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1637,'Cinematic','CTCS','411',4,'Film, Television and Cultural Studies','Detailed examination of film/television from the perspectives and insights of Cultural Studies; focus on the production and reception of cultural texts, practices, and communities.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1638,'Cinematic','CTCS','412',4,'Gender, Sexuality and Media','Examines how gender and sexuality are figured in cinema and television with an emphasis on the development of feminist media theory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1639,'Cinematic','CTCS','464',4,'Film and/or Television Genres','Rigorous examination of film and/or television genres: history, aesthetics, cultural context, social significance, and critical methodologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1640,'Cinematic','CTCS','466',4,'Theatrical Film Symposium','Lectures and readings on creative problems in the motion picture industry; current films; interviews with visiting producers, directors, writers, performers.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1641,'Cinematic','CTCS','467',4,'Television Symposium','Lectures and readings on creative problems in the television industry; study of current and historical trends, interviews with producers, directors, writers and performers.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1642,'Cinematic','CTCS','469',4,'Film and/or Television Style Analysis','Intensive study of the style of an auteur, studio, film or television making mode in terms of thematic and formal properties and their influences upon the art of film.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1643,'Cinematic','CTCS','473',4,'Film and Media Theory','Influential ideas and theoretical approaches that have shaped the making and study of film. Students are encouraged to take this course in their junior year.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1644,'Cinematic','CTCS','494',4,'Advanced Cinema and Media Studies Seminar','Rotating topics involving detailed study of the historical, cultural and aesthetic analysis of film, television, and new media technologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1645,'Cinematic','CTCS','500',4,'Seminar in Film Theory','Introduction to classical and contemporary film theory; exploration of their relationship to filmic experimentation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1646,'Cinematic','CTCS','502',2,'World Cinema After 1945','Historical survey of film from a global perspective from 1945 until the present.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1647,'Cinematic','CTCS','504',2,'Survey of Television History','An exploration of the historical, cultural, business, creative, and technological aspects of television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1648,'Cinematic','CTCS','505',2,'Survey of Interactive Media','A survey course exploring the historical, cultural, business, creative, and technological aspects of the new interactive media.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1649,'Cinematic','CTCS','506',2,'Critical Studies Colloquium/Professional Seminar','Provides orientation to the profession, opportunities for academic and professional growth and development. Recommended for entering students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1650,'Cinematic','CTCS','510',4,'Case Studies in National and/or Regional Media','Seminar on media\'s impact in defining nation and/or region in specific cultural contexts. Also addresses issues of exile, diaspora, transnationalism and globalism. Departmental approval required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1651,'Cinematic','CTCS','520',4,'Film History Through the Archives and Special Collections','A historical exploration of film and television through the artifacts, images and writings used in their creation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1652,'Cinematic','CTCS','564',4,'Seminar in Film and Television Genres','Advanced study of a selected genre of film and/or television its relationship to history, society, and culture, as well as to genre theory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1653,'Cinematic','CTCS','673',4,'Topics in Theory','Contemporary theoretical frameworks and their relationship to film and television studies. Topics differ from semester to semester.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1654,'Cinematic','CTCS','678',4,'Seminar in Film Theory and Medium Specificity','Explores the way film has been theorized in relationship to traditional media that preceded it and electronic media that followed.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1655,'Cinematic','CTCS','790',1,'Research','Research leading to the doctorate. Maximum units which may be applied to the degree to be determined by the department.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1656,'Cinematic','CTCS','794a',2,'Doctoral Dissertation','Credit on acceptance of Dissertation. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1657,'Cinematic','CTCS','794b',2,'Doctoral Dissertation','Credit on acceptance of Dissertation. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1658,'Cinematic','CTCS','794c',2,'Doctoral Dissertation','Credit on acceptance of Dissertation. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1659,'Cinematic','CTCS','794d',2,'Doctoral Dissertation','Credit on acceptance of Dissertation. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1660,'Cinematic','CTCS','794z',0,'Doctoral Dissertation','Credit on acceptance of Dissertation. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1661,'Cinematic','CTIN','101',2,'Fundamentals of Procedural Media','Introduction to the procedural nature of interactive media and games, though the coding language Processing. Students will develop proficiency in reading and creating computational media.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1662,'Cinematic','CTIN','190',4,'Introduction to Interactive Entertainment','Critical vocabulary and historical perspectives on interactive entertainment; students articulate their own ideas, while wrestling with the larger conceptual issues at play within the field.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1663,'Cinematic','CTIN','290',4,'Digital Media Workshop','Exploration of a variety of moving image aesthetics and methodologies in order to highlight how the language of cinema shapes contemporary digital and interactive media practices.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1664,'Cinematic','CTIN','406L',2,'Sound Design for Games','Introduction to the techniques, terminology, and implementation of sounds in games, including establishing a sense of place and concepts of realistic sound.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1665,'Cinematic','CTIN','452L',4,'Themed Entertainment Design','The fundamentals of design, technology, operations and process for the creation of themed entertainment experiences and story-centric place-making. Prerequisite: CTIN 488 or CTIN 541. <strong>Recommended preparation:</strong> CTIN 489 or CTIN 532.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1666,'Cinematic','CTIN','458',2,'Business and Management of Games','Overview of current business models in games and interactive media, methods for pitching and getting products funded; copyright and intellectual property.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1667,'Cinematic','CTIN','463',4,'Anatomy of a Game','Examine two game products from concept to delivery; introduce students to each of the professional disciplines involved in making digital games. <strong>Recommended preparation:</strong> CTIN 488.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1668,'Cinematic','CTIN','464',2,'Game Studies Seminar','Rigorous examination of interactive entertainment: genres, history, aesthetics, cultural context, and social significance. Topics vary by semester.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1669,'Cinematic','CTIN','482',2,'Designing Social Games','Leading industry techniques and networking fundamentals; designing an original social game.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1670,'Cinematic','CTIN','483',4,'Introduction to Game Development','Introduction to technical and creative aspects of game development, including the art of creating the digital game prototype and development of games; coding experience required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1671,'Cinematic','CTIN','484L',2,'Intermediate Game Development','Advanced topics in game programming and implementation such as using game engines, creating digital prototypes, player controls and level design.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1672,'Cinematic','CTIN','485L',2,'Advanced Game Development','Advanced concepts in 3-D game development: story and character progression, emergent game-play, comprehensive game mechanics, and artificial intelligence.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1673,'Cinematic','CTIN','488',4,'Game Design Workshop','An introduction to making games. Students will explore the principles of game design through the entirely analog creation of card, board and tabletop games.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1674,'Cinematic','CTIN','489',2,'Intermediate Game Design Workshop','A follow-up to the introductory game design class, this course will introduce more advanced concepts in game design and game theories, including ideation, digital prototyping and level design.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1675,'Cinematic','CTIN','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1676,'Cinematic','CTIN','491L',4,'Advanced Game Project I','Students work in teams on pre-production and production of a functional digital game suitable for showcases, festivals, and further development.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1677,'Cinematic','CTIN','497',1,'Interactive Media Startup','Pitching, production planning, forming a company and seeking funding for your creative media idea. Duplicates credit in former CTIN 497ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1678,'Cinematic','CTIN','499',2,'Special Topics','Detailed investigation of new or emerging aspects of cinema, television, and/or interactive media; special subjects offered by visiting faculty; experimental subjects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1679,'Cinematic','CTIN','506',2,'Procedural Expression','Developing procedural literacy in the analysis and creation of computational media; an introduction to how we create meaningful experiences using rules and interaction. Duplicates credit in former CTIN 400.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1680,'Cinematic','CTIN','511',1,'Interactive Media Seminar','Seminars on latest trends in interactive media content, technology, tools, business and culture. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1681,'Cinematic','CTIN','532L',4,'Interactive Design and Production I','The development of interactive experiences with an emphasis on prototyping and development. Open to Interactive Media MFA students only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1682,'Cinematic','CTIN','534L',4,'Experiments in Interactivity I','Experimental studio course to explore concepts of structure, aesthetics and content of interactive experience design.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1683,'Cinematic','CTIN','541',2,'Design for Interactive Media','Practical exploration and practicum on the fundamental technical and aesthetic principles in the design of interactive media. Students will develop design and prototyping skills.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1684,'Cinematic','CTIN','590',1,'Directed Research','Research project leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1685,'Cinematic','CTIN','591',2,'Advanced Development Project','Students from many disciplines work in large teams to create an innovative, large-scale interactive media or game project. <strong>Recommended preparation:</strong> CTCS 505, CTIN 541.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1686,'Cinematic','CTIN','594a',2,'Master\'s Thesis','Credit on acceptance of theis. Graded IP/CR/NC. Open to M.F.A., Interactive Media majors only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1687,'Cinematic','CTPR','288',2,'Originating and Developing Ideas for Film','Exercises in observation, imaginative association, visualization, etc., that deepen the creative process, leading to ideas, stories, characters, and images for narrative, documentary, and experimental films.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1688,'Cinematic','CTPR','290',4,'Cinematic Communication','Introduction to the interrelationship of visuals, sound, and editing in cinematic communication. Workshops in directing and producing. Individual and group projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1689,'Cinematic','CTPR','294',4,'Directing in Documentary, Fiction and New Media','Basic concepts of directing in new media, documentary and fictional narrative. Includes work with actors, documentary concepts, and creation of short new media projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1690,'Cinematic','CTPR','295L',4,'Cinematic Arts Laboratory','The aesthetics and tools of the major disciplines of cinematic arts: producing, cinematography, sound, and editing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1691,'Cinematic','CTPR','310',4,'Intermediate Production','Principles of visual and aural communication; idea development and realization using image, movement, pace, the spoken word and other sounds; small crew projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1692,'Cinematic','CTPR','327',3,'Motion Picture Cinematography','Use of high definition motion picture equipment to explore the fundamentals of shot design, movement and lighting. In class group projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1693,'Cinematic','CTPR','335',3,'Motion Picture Editing','Theory, techniques, and practices in picture editing; use of standard editing equipment; individual projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1694,'Cinematic','CTPR','340',2,'Creating the Motion Picture Sound Track','Techniques and aesthetics for recording production sound, editing dialogue, sound effects, music, Foley and preparing for the mix. For film, television, and other media.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1695,'Cinematic','CTPR','371',4,'Directing for Television','Preparation of director\'s preproduction blockout; study of direction for live, tape, and film production, for both dramatic and informational television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1696,'Cinematic','CTPR','385',4,'Colloquium: Motion Picture Production Techniques','Basic procedures and techniques applicable to production of all types of films; demonstration by production of a short film from conception to completion.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1697,'Cinematic','CTPR','386',4,'Art and Industry of the Theatrical Film','Detailed analysis of one theatrical film from conception through critical reception to develop an understanding of motion pictures as art, craft, and industry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1698,'Cinematic','CTPR','409',2,'Practicum in Television Production','Television production: laboratory course covers operating cameras, creating graphics, technical operations, controlling audio and floor-managing live productions. Students plan and produce actual Trojan Vision programs.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1699,'Cinematic','CTPR','410',2,'The Movie Business: From Story Concept to Exhibition','Examination of the industry from story ideas, through script development, production and exhibition; evaluation of roles played by writers, agents, studio executives, marketing and publicity.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1700,'Cinematic','CTPR','421',2,'Practicum in Editing','Workshop in how editing can shape storytelling, using content from a variety of media and in various styles. Modern non-linear equipment and techniques.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1701,'Cinematic','CTPR','422',2,'Makeup for Motion Pictures','Lecture-laboratory in makeup relating it to mood of the story and emulsion of the camera stock.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1702,'Cinematic','CTPR','423',2,'Introduction to Special Effects in Cinema','Introductory workshop in the aesthetics and practices of special effects, embracing both the classical and contemporary modes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1703,'Cinematic','CTPR','424',2,'Practicum in Cinematography','Camera and lighting workshop in color cinematography, beginning with digital still photography and moving into 16mm and advanced digital cameras. In-class exercises.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1704,'Cinematic','CTPR','425',2,'Production Planning','Theory, discussion, and practical application of production planning during preproduction and production of a film.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1705,'Cinematic','CTPR','426',2,'The Production Experience','To provide students with basic working knowledge of both the skills of the motion picture set and production operations through classroom lectures and hands-on experience.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1706,'Cinematic','CTPR','427',2,'Introduction to Color Grading','Exploration of the various aspects of color grading and how it can enhance storytelling.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1707,'Cinematic','CTPR','431',2,'Developing the Documentary Production','The tools and skills necessary to turn an idea into a documentary story, using sample reels, pitches, and writing to develop a professional proposal.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1708,'Cinematic','CTPR','438',2,'Practicum in Producing','A comprehensive overview of the role of the producer. How projects are conceived, developed, packaged, financed and marketed.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1709,'Cinematic','CTPR','440',2,'Practicum in Sound','Aesthetic and technical skills of production and post-production sound necessary to create a motion picture soundtrack.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1710,'Cinematic','CTPR','441',2,'Sound Design','The historical, theoretical, and practical aspects of sound design for genre films. Exploration of the techniques and processes for creating sounds that do not exist.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1711,'Cinematic','CTPR','447',2,'Producing Visual Effects','The complex creative, technical, financial, logistical and political challenges of incorporating visual effects into film projects from initial pre-production through final delivery.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1712,'Cinematic','CTPR','450',2,'The Production and Post-Production Assistant','Detailed view of the process of making media. Introducing fundamental thinking typical of each craft. Weekend crew experience and responsibilities. Prerequisite: CTPR 310. Open only to Cinematic Arts Film and Television Production majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1713,'Cinematic','CTPR','454',4,'Acting for Film and Television','Intensive examination of skills and techniques necessary for successful performances in film and television. Practical application through in-class exercises and assigned projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1714,'Cinematic','CTPR','456',2,'Introduction to Art Direction','Introduction to drafting, set design, set decoration and creating models for students with diverse abilities. Guest lectures, group discussions and hands-on workshop.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1715,'Cinematic','CTPR','457',2,'Creating Poetic Cinema','An investigation of poetic cinema from four different perspectives: found poetry; applied poetry; poetry as image; and poetry in narrative fiction. Production of short films.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1716,'Cinematic','CTPR','460',2,'Film Business Procedures and Distribution','Financing, budgeting, management as applied to films; problems of distribution, including merchandising, cataloging, evaluation, and film library management.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1717,'Cinematic','CTPR','461',2,'Managing Television Stations and Internet Media','Managing electronic media, including radio and television stations, broadcast and cable networks, and the internet.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1718,'Cinematic','CTPR','465',2,'Practicum in Production Design','Practical instruction in script breakdown, location surveys, traditional and computer-aided working drawings, model making, set decoration, construction and scenic departments.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1719,'Cinematic','CTPR','470',4,'Practicum in On-Screen Direction of Actors','Concentration on the basic skills in working with actors from a director\'s point of view.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1720,'Cinematic','CTPR','474',4,'Documentary Production','Pairs produce, direct, shoot, and edit a short documentary on a subject of their choice. Finished projects will be suitable for broadcast/festivals.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1721,'Cinematic','CTPR','476',2,'Directing The Comedic Scene','Directing comedy: casting, rehearsing, directing actors, scene analysis, staging, shooting, and editing, leading to the filming of a two-person comic scene',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1722,'Cinematic','CTPR','478',2,'Practicum in Directing','Concepts of directing for motion pictures, emphasizing the working relationship of actors and directors. Scenes will be staged in class and filmed for class presentation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1723,'Cinematic','CTPR','480',4,'Advanced Production Workshop','Directors, producers, cinematographers, editors and sound designers collaborate to produce, shoot, edit and deliver fictional, documentary or experimental projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1724,'Cinematic','CTPR','484',4,'Advanced Multi-Camera Television Workshop','Exercises and practical application for writing and producing a multi-camera television project. Special attention to the development of the sitcom.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1725,'Cinematic','CTPR','487',2,'Recording for Film Scores','Exploration of the role of the recording studio in professional film and video productions. Emphasis on technical and hardware considerations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1726,'Cinematic','CTPR','496',2,'The Film Industry: Career Challenges and Choices for Women','Discusses womens\' roles in the entertainment industry and career opportunities available for women in the business, corporate, and creative sectors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1727,'Cinematic','CTPR','497',2,'Music Video Production','Writing the concept, budgeting, shooting, editing and directing a music video. Also covered: getting the job, dealing with the band, working with the record company.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1728,'Cinematic','CTPR','506',2,'Visual Expression','Exploring the relationship between story structure and visual structure, discussion of aesthetics and technical controls, in-class examples and screenings, photographic assignments.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1729,'Cinematic','CTPR','507',4,'Production I','The effective communication of ideas through the language of cinema; one directing exercise; two short HD projects; introductions to producing, directing, editing, cinematography, and sound.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1730,'Cinematic','CTPR','508',6,'Production II','Practicum in group production, emphasizing the collaborative process and the expressive use of sound and image. Prerequisites: CTPR-507 and CTPR-510 and CTWR-505.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1731,'Cinematic','CTPR','523',2,'Introduction to Multiple-Camera Production','How to direct comedy or dramatic scenes, using multiple camera techniques. Students also serve as crew members, learning lighting, mixing, studio controls, and stage management.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1732,'Cinematic','CTPR','531',2,'Planning the Documentary Production','A preproduction workshop for the research and planning of a documentary project to be executed in CTPR 547 or an advanced production workshop class. Prerequisite: CTPR 508.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1733,'Cinematic','CTPR','532',2,'Intermediate Directing','Practical experience in staging dramatic narrative scenes, emphasizing directing actors, rehearsal techniques and camera blocking.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1734,'Cinematic','CTPR','533',2,'Directing Techniques','Practicum in more complex directing issues concentrating both on performance and exploration of shaping scenes visually through blocking of action and placement of camera.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1735,'Cinematic','CTPR','534',2,'Intermediate Production Design','Exercises concentrating on script analysis, blocking the scene, ground plans, elevations, visual research and concepts, departmental collaboration, and presentation of work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1736,'Cinematic','CTPR','535',2,'Intermediate Editing','Editorial construction of sequences to analyze the interrelationships of the various film elements, both visual and aural.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1737,'Cinematic','CTPR','537',2,'Intermediate Cinematography','Close study through practical in class exercises of the technical and aesthetic principles of cinematography. Introduction to 16mm film and advanced digital cameras.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1738,'Cinematic','CTPR','538',2,'Intermediate Producing','Definition, examination and practical experience in the role of the line producer as it relates to preproduction, production and post production.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1739,'Cinematic','CTPR','546L',6,'Production III, Fiction','An intensive workshop experience in which students, crewing in their area of specialization, complete the shooting and postproduction of projects up to thirty minutes in length. Qualifying courses: for directors, CTPR 532, and for cinematographers, CTPR 537.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1740,'Cinematic','CTPR','547L',6,'Production III, Documentary','Intensive workshop; students shoot and finish documentary projects up to about 25 minutes. Qualifying courses: for directors, CTPR 431 or CTPR 531; for cinematographers, CTPR 424 or CTPR 537.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1741,'Cinematic','CTPR','552',2,'Advanced Directing','An advanced production class in directing. Encounters with experienced directors; and individual student production of a short. <strong>Recommended preparation:</strong> CTPR 533.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1742,'Cinematic','CTPR','553',1,'Developing the Advanced Project','Script workshop for advanced projects. Covers key screenplay elements, including protagonist and objective, conflict, obstacles, premise and opening, main tension, emotional throughline, etc.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1743,'Cinematic','CTPR','557',2,'Advanced Cinematography','Advanced camera and lighting techniques for those considering a professional career in cinematography.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1744,'Cinematic','CTPR','558',2,'Advanced Producing','Defines and examines the role of the Executive/Feature Producer through the preproduction, production and post production phases. Prerequisites: CTPR-538 or one of the following in equivalent crew position: CTPR 486, CTPR 546, CTPR 547.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1745,'Cinematic','CTPR','563',4,'The Business of Representation','Various roles an agent, manager, attorney and publicist play in representing talent, producers and writers. Taught by professionals who are at the forefront of the entertainment industry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1746,'Cinematic','CTPR','565',2,'Making Media for Social Change','Each student will produce and direct a film incorporating a social issue of his/her choice into the narrative of the film. Prerequisites: CTPR 310 or CTPR 508.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1747,'Cinematic','CTPR','566',2,'Developing and Selling Your Film and TV Projects','Developing, pitching, and selling your feature motion picture and TV projects. Open to undergraduate senior and third-year graduate cinema majors only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1748,'Cinematic','CTPR','572',2,'The World of Television: From Concept to Air and Everything in Between','Takes projects from conception to sale, including development, production, post-production, and marketing. Students will develop original projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1749,'Cinematic','CTPR','573',1,'Producing the Advanced Project','Basic skills of production planning as applied to students\' advanced project scripts. Covers all steps from breakdown to delivery. Departmental approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1750,'Cinematic','CTPR','581a',4,'Individual Production Workshop','Individual experimental projects involving the creative use of visuals (live action or animated) and sound. Qualifying courses: CTPR 532 (for directors); CTPR 558 (for producers). Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1751,'Cinematic','CTPR','581b',2,'Individual Production Workshop','Individual experimental projects involving the creative use of visuals (live action or animated) and sound. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1752,'Cinematic','CTPR','581c',2,'Individual Production Workshop','Individual experimental projects involving the creative use of visuals (live action or animated) and sound. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1753,'Cinematic','CTPR','581z',0,'Individual Production Workshop','Individual experimental projects involving the creative use of visuals (live action or animated) and sound. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1754,'Cinematic','CTPR','582a',2,'Advanced Production Seminar','Advanced individual film or video projects under the guidance of a faculty mentor, without benefit of university equipment or resources. Qualifying courses: CTPR 532 (for directors); CTPR 558 (for producers). Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1755,'Cinematic','CTPR','582b',2,'Advanced Production Seminar','Advanced individual film or video projects under the guidance of a faculty mentor, without benefit of university equipment or resources. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1756,'Cinematic','CTPR','582z',0,'Advanced Production Seminar','Advanced individual film or video projects under the guidance of a faculty mentor, without benefit of university equipment or resources. Open to Production students only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1757,'Cinematic','CTPR','585a',2,'Advanced Producing Project','Supervised, individual study leading to the creation of a producer\'s package. The package will include script, schedule, budget, finance and marketing plan. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1758,'Cinematic','CTPR','585b',2,'Advanced Producing Project','Supervised, individual study leading to the creation of a producer\'s package. The package will include script, schedule, budget, finance and marketing plan. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1759,'Cinematic','CTPR','585z',0,'Advanced Producing Project','Supervised, individual study leading to the creation of a producer\'s package. The package will include script, schedule, budget, finance and marketing plan. Graded IP/Letter.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1760,'Cinematic','CTWR','100g',4,'Story: Character, Conflict, and Catharsis','Fundamentals course on the elements of scene writing, story structure, and character development.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1761,'Cinematic','CTWR','206a',4,'Writing the Screenplay','Development of synopsis and treatment for a theatrical or documentary screenplay: theme, plot, character, mise-en-scene and utilization of cinematic elements. Filmic Writing students only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1762,'Cinematic','CTWR','211g',4,'The Television Writer: An Agent of Change','The television writer as an agent of change across current social issues including, but not limited to: race, gender, and class.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1763,'Cinematic','CTWR','305',4,'Advanced Screenwriting: The Relationship Screenplay','An inquiry into the complex nature of human relationships by writing an original feature length screenplay that examines the multi-dimensional world of characters ant the ties that bind them together. Open only to Writing for Screen and Television majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1764,'Cinematic','CTWR','321',2,'Introduction to Hour-Long Television Writing','The fundamentals of writing for dramatic episodic television. Writing scenes from popular television shows and examination of television story structure. Prerequisite: CTWR-100, CTWR 402, CTWR 412 or CTWR 413.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1765,'Cinematic','CTWR','324',2,'Introduction to Half-Hour Television Writing','The fundamentals of writing for comedic episodic television. Writing scenes from popular comedy series and examination of half-hour television comedy story structure.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1766,'Cinematic','CTWR','404',2,'Foundations of Comedy','Study of comedy theory and practical applications in film, television, and social media. Lectures and screenings of comedic forms tracing past, present and future.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1767,'Cinematic','CTWR','407',2,'Creating the Comedic Character','Utilization of various techniques for character to emerge naturally in scene and stories. Creating multiple comedic characters to generate future stories. <strong>Recommended preparation:</strong> CTWR 404.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1768,'Cinematic','CTWR','410L',4,'Character Development and Storytelling for Games','An exploration of characters and story worlds as they relate to gaming with an emphasis on emotionally rich environments in interactive enetertainment. <strong>Recommended preparation:</strong> CTIN 488',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1769,'Cinematic','CTWR','412',2,'Introduction to Screenwriting','Introduction to the formal elements of writing the short film.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1770,'Cinematic','CTWR','413',2,'Writing the Short Script I','Preparation of scripts for short films: form, structure, planning. Open only to Cinematic Arts Film and Television Production majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1771,'Cinematic','CTWR','414',2,'The Screenplay','Students study story structure and develop several story outlines, write a short script for possible production, a feature film outline and first act. <strong>Recommended preparation:</strong> CTWR-416. Open only to Cinematic Arts Film and Television Production majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1772,'Cinematic','CTWR','415a',2,'Advanced Writing','Principles of the feature film; creating theme, character and structure that combine into a feature-length story treatment. <strong>Recommended preparation:</strong> CTWR 416.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1773,'Cinematic','CTWR','416',2,'Motion Picture Script Analysis','Critical analysis of story structure from classic films to contemporary works. Identification of key story concepts and elements of three-act structure. 2.0, not repeatable.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1774,'Cinematic','CTWR','417',2,'Script Coverage and Story Analysis','Evaluation of completed scripts prior to their production. Coverage and analysis of scripts as potential properties from the perspective of a production company.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1775,'Cinematic','CTWR','418a',4,'Senior Thesis','a: Creation of feature-length treatment and first draft Senior Thesis screenplay, including \'pitching\' experiences. B: Completion and revision of Senior Thesis project and introduction to motion picture industry procedures and practices through interaction with industry representatives. Open to B.F.A. Writing for Screen and Television students only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1776,'Cinematic','CTWR','419a',4,'Senior Thesis in Dramatic Television','Advanced workshop developing an original hour-long dramatic series including characters, world, and story lines for season one. Final assignment is completed hour-long pilot episode. <strong>Recommended preparation:</strong> CTWR-411.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1777,'Cinematic','CTWR','421',2,'Writing the Hour-Long Dramatic Series','Writing an episode of an existing dramatic television series within the hour-long format with an emphasis on conception, pitching, characterization and structure.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1778,'Cinematic','CTWR','431',2,'Screenwriters and Their Work','Detailed investigation of a specific screenwriter\'s style and the works they\'ve influenced. Lectures include screenings and visiting screenwriters.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1779,'Cinematic','CTWR','434',2,'Writing the Half-Hour Comedy Series','Writing an episode of an existing half-hour comedy series, with emphasis on the anatomy of a joke, comedic structure, and character.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1780,'Cinematic','CTWR','437',4,'Writing the Original Situation Comedy Pilot','Advanced workshop for writing an original half-hour comedy series, including a pilot script, summary of characters, and story lines for first season.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1781,'Cinematic','CTWR','438',2,'Linked Narrative Storytelling for the Web','Create, develop, and execute episodic video content for the web. Focus on content and characters that are viable in the internet landscape.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1782,'Cinematic','CTWR','439',4,'Writing the Original Dramatic Series Pilot','An advanced workshop in which students create an original dramatic series, including a first script and a summary of characters and story lines. Prerequisite: CTWR-421 or CTWR-434.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1783,'Cinematic','CTWR','487',4,'Staff Writing the Multi-Camera Television Series','Working on the writing staff of an original multi-camera television series, with emphasis on the writers\' room experience and how to executive produce an episode.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1784,'Cinematic','CTWR','489',2,'Straight to Series: Writing and Producing the Episodic Drama','Staff writing and producing the original episodic drama, from development to post-production.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1785,'Cinematic','CTWR','502',1,'Graduate Writing Symposium','A survey of the creative and professional range of the working screenwriter. <strong>Recommended preparation:</strong> CNTV 501.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1786,'Cinematic','CTWR','505',2,'Creating the Short Film','Strengthening and deepening the ability to conceive and develop ideas that will lead to compelling, authentic, and personally meaningful films. Duplicates credit in CTWR 528.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1787,'Cinematic','CTWR','513',2,'Writing the Short Script','Preparation of scripts for short films; dramatic, informational, experimental, and other forms. Open to screenwriting (CNTV) and dramatic writing (THTR) majors only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1788,'Cinematic','CTWR','514a',2,'Basic Dramatic Screenwriting','Dramaturgy for the fiction and nonfiction work. Techniques for creating the original or adapted theatrical length script. a: Emphasizes narrative development, through short scripts, sequences, and story outlines. Open to screenwriting (CNTV) and dramatic writing (Theatre) majors only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1789,'Cinematic','CTWR','515a',4,'Practicum in Screenwriting','a: Creation of a feature screenplay from presentation through treatment, including some scene work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1790,'Cinematic','CTWR','516',2,'Advanced Motion Picture Script Analysis','Critical analysis of the structure of films from the classics to current award winners. Students will learn how to identify key story concepts and break down three act structure in finished films and scripts.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1791,'Cinematic','CTWR','517a',4,'Thesis in Half-Hour Television Comedy','Developing an original half-hour comedy television series, including characters, world and storylines for season one. Completion of a first draft script, polish, and series bible. Prerequisites: CTWR 514b. Corequisite: CTWR 534. Open only to Writing for Screen and Television master students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1792,'Cinematic','CTWR','519a',4,'Thesis in Television Drama','Part A -- writing the pilot -- is an intensive workshop in which master\'s students develop an original television 1-hour series including characters, world and storylines for season one. Final assignment is the finished pilot episode of the series. Prerequisite: CTWR 514b. Corequisite: CTWR 521. Open only to master students in Writing for Screen and Television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1793,'Cinematic','CTWR','521',2,'Advanced Hour-Long Television Drama','Writing the first draft and revision of an episode of an existing dramatic television series. Corequisite: CTWR 514a. Open only to master students in Theatre (Dramatic Writing) and in Writing for Screen and Television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1794,'Cinematic','CTWR','526',2,'Advanced Storytelling for Interactive Media','Advanced writing workshop addressing issues specific to interactive storytelling regarding construction of narrative and character as seen in games and other forms of interactive media. Prerequisite: CTWR 518.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1795,'Cinematic','CTWR','529',2,'Intermediate Screenwriting','Emphasizes structural elements crucial to the feature film. Techniques for creating an original or adapted theatrical-length script.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1796,'Cinematic','CTWR','533a',4,'Writing the Feature Script','Advanced screenwriting workshop. Development of characters and story to complete a treatment and first draft of a feature length script.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1797,'Cinematic','CTWR','533b',4,'Writing the Feature Script','Advanced screenwriting workshop. b: Rewriting the first draft into a second draft through advanced work-shopping of script. A third draft polish is the final assignment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1798,'Cinematic','CTWR','534',2,'Advanced Half-Hour Television Comedy','Advanced workshop in writing the first draft and revision of an episode of an existing comedic television series. The comedy writing room will be emphasized. Corerequisite: CTWR 514a. Open only to master students in Theatre (Dramatic Writing) and Writing for Screen and Television.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1799,'Cinematic','CTWR','541',2,'Dreams, the Brain, and Storytelling','Examination of the links between the brain, creativity, neuroscience and storytelling. Through lectures, screenings, and readings, students will log dreams for use in creative exercises.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1800,'Cinematic','CTWR','543',4,'The Character-Driven Screenplay','Advanced screenwriting wherein a first draft of a feature-length script will be developed and written with an emphasis on character as story engine. Prerequisite: CTWR 533b. <strong>Recommended preparation:</strong> CTWR 516. Open only to master students in Cinematic Arts Film and Television Production (CAPR).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1801,'Cinematic','CTWR','549',4,'Advanced Rewriting Workshop in Hour-Long Drama','Advanced television writing, emphasizing the rewrite of a first draft pilot script and series bible for an hour-long television series.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1802,'Cinematic','CTWR','553',4,'Advanced Rewriting Workshop','Advanced feature screenwriting, emphasizing the rewrite of a first draft script.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1803,'Cinematic','CTWR','555',2,'Pitching for Film and Television','Development and pitching of ideas for motion pictures, episodic and television pilots. Reducing ideas to basic components; enhancing verbal presentation skills.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1804,'Cinematic','CTWR','559',2,'The Business of Writing for Screen and Television','Examination and in-depth analysis of the studio system, the television development process, literary representation, new emerging markets, and the Writers Guild of America.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1805,'Cinematic','CTWR','572',2,'Practicum in Directing Actors for Film','Seminar in directing actors for film; emphasis on demonstration and laboratory exercises, script analysis, and study and character motivations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1806,'Cinematic','CTWR','585',2,'Advanced Genre Writing','Development and execution of writing in various genres of film, television, or new media scripts. Prerequisite: CTWR 514b.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1807,'Cinematic','IML','104',2,'Introduction to Digital Studies','An introduction to the expressive range of screen languages in their cultural, historical, and technological contexts.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1808,'Cinematic','IML','140',2,'Workshop in Multimedia Authoring','Introduction to the expressive potential of multimedia as a critical and creative tool, supplementing traditional forms of academic work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1809,'Cinematic','IML','201',4,'The Languages of Digital Media','An in-depth investigation of the close interrelationships among technology, culture and communication to form a solid foundation for digital authoring. Duplicates credit in former IML 101.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1810,'Cinematic','IML','202',4,'Media Arts and Practice Studio I','Intermediate laboratory exploring the foundations of visual culture through a series of critical design prompts. Emphasis on the creative process, project documentation and the development of a personal design methodology.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1811,'Cinematic','IML','230',4,'Fundamentals of Media Design','Introduction to the methods of visual design with respect to digital media authorship. Exploring the creative process through various conceptual workflows and hands-on techniques.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1812,'Cinematic','IML','288',4,'Critical Thinking and Procedural Media','Investigation of the potentials of computational media to define new aesthetics, modes of representation and structures of communication.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1813,'Cinematic','IML','295Lm',4,'Race, Class and Gender in Digital Culture','Critical analysis of the categories of race, class and gender within the diverse digital spaces of contemporary culture, from video games to the digital divide.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1814,'Cinematic','IML','300',4,'Reading and Writing the Web','An introduction to a broad range of technical and theoretical issues surrounding the production of web-based content.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1815,'Cinematic','IML','310',2,'Professionalism for Media Arts','Development of documentation and archival strategies, with an emphasis on techniques of personal and professional representation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1816,'Cinematic','IML','328',2,'Sonic Media Art','Survey of audio culture and sound technologies, focusing on developing sonic literacy and creating artwork using sound as a primary modality.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1817,'Cinematic','IML','335',2,'Digital Narrative Design I','An introduction to audio and video techniques for digital storytelling across various platforms. Students will create multiple short-form projects using contemporary tools and technologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1818,'Cinematic','IML','340',4,'Remixing the Archive','An intermediate level course which approaches archived material from multiple perspectives, in order to develop new avenues of expression, education, and research.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1819,'Cinematic','IML','346',2,'Methods in Digital Research','Emphasizing rigorous multimedia research and authorship strategies, this course prepares students to undertake large-scale digital projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1820,'Cinematic','IML','354',2,'Introduction to 3-D Modeling','An introduction to the history, theory and critical context of 3-D spatial representation, as well as foundational authoring skills in modeling interactive 3-D spaces.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1821,'Cinematic','IML','400',4,'Creative Coding for the Web','Analysis and development of scholarly media projects using diverse web authoring strategies, technologies and documentation. Prerequisites: IML 300.',1);
