INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1823,'Cinematic','IML','422',4,'Information Visualization','Visualizing information through diverse media platforms, with a focus on critical analysis and hands-on visualization.',1);
