INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1864,'Annenberg','CMGT','554',4,'Copywriting and Creativity','Foundational and advanced practices for copywriting and related design incommunication; integrated analysis of concepts and pragmatics surrounding creativity forcommunication effectiveness',1);
