INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2331,'Viterbi','AME','331',3,'Heat Transfer','General principles underlying heat transfer by conduction, convection, and radiation; steady and transient conditions; heat exchangers. Prerequisite: AME-310. Co-requisite: AME-309 or CE-309.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2332,'Viterbi','AME','341a',3,'Mechoptronics Laboratory I and II','A coordinated laboratory and lecture sequence on aeromechanical instrumentation and device control stressing the symbiotic integration of mechanical, optical and electronic components.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2333,'Viterbi','AME','404',3,'Computational Solutions to Engineering Problems','Mathematical aspects of the solutions to typical advanced mechanical engineering problems. Modeling, simulation, computational aspects, computer solutions, and computational tools. <strong>Recommended preparation:</strong> FORTRAN, MATLAB, and Maple',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2334,'Viterbi','AME','408',3,'Computer-Aided Design of Mechanical Systems','Design of mechanical systems using advanced graphics techniques; computer-aided drafting, design optimization, elements of computer graphics, solids modeling; introduction to computer-aided manufacturing. Prerequisite: AME 204 or CE 225. <strong>Recommended preparation:</strong> AME 308.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2335,'Viterbi','AME','410',3,'Engineering Design Theory and Methodology','Product planning and task clarification, voice of customers, quality function deployment, conceptual and embodiment design, axiomatic theory of design, product quality and manufacturability, design decision-making. Junior standing. <strong>Recommended preparation:</strong> AME 305',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2336,'Viterbi','AME','415',3,'Turbine Design and Analysis','Physics of turbine operation; design and analysis for the development of turbine hardware for propulsion and power generation. <strong>Recommended preparation:</strong> Familiarity with Matlab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2337,'Viterbi','AME','430',3,'Thermal Systems Design','Design methodology for thermal systems; boilers, condensers, air conditioning, power generation, air pollution control, combustion and alternative fuels. Prerequisite: AME-331; <strong>Recommended preparation:</strong> AME-312.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2338,'Viterbi','AME','441aL',3,'Senior Projects Laboratory','Individual engineering projects designed and constructed to model and test a physical principle or system.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2339,'Viterbi','AME','451',3,'Linear Control Systems I','Transform methods, block diagrams; transfer functions; stability; root-locus and frequency domain analysis and design; state space and multiloop systems. Prerequisites: AME 302 and MATH-245. (Duplicates credit in EE-482)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2340,'Viterbi','AME','457',3,'Engineering Fluid Dynamics','Laminar and turbulent boundary layer flow with and without heat transfer; boundary layer separation, stability, transition and control; introduction to compressible fluid flow.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2341,'Viterbi','AME','459',3,'Flight Mechanics','Applications of basic aerodynamics to aircraft and missile performance, power and thrust, stability and control, compressibility effects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2342,'Viterbi','AME','461',3,'Formation Evaluation','Concepts of petroleum geology, interpretation of downhole surveys and measurements including well logs, MWD, mud logs and samples',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2343,'Viterbi','AME','463L',3,'Introduction to Transport Processes in Porous Media','Properties of porous rocks; capillarity effect, single-phase and multiphase fluid flow through porous media; diffusion and dispersion, miscible displacement, heat transfer. Lecture, 3 hours; laboratory, 3 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2344,'Viterbi','AME','465L',3,'Drilling Technology and Subsurface Methods','Theory and practice in drilling technology; mechanical properties of reservoir rocks; well completion; acidizing and fracturing, oilproduction gechnology. Lecture, 3 hours; laboratory, 3 hours',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2345,'Viterbi','AME','485',3,'Aerospace Structures I','Design, qualification, and workmanship verification of aerospace structures. Analysis for assessing buckling and crippling, welds, and analysis of connections and thin-walled structures.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2346,'Viterbi','AME','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2347,'Viterbi','AME','491',1,'Undergraduate Design Projects II','Analysis, design, fabrication, and evaluation of devices intended for entry in local and national design competitions. Intended for students with prior project experience. Upper division standing. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2348,'Viterbi','AME','502',3,'Modern Topics in Aerospace Design','Current topics in Aerospace Engineering are addressed by a number of industry panelists. Students, under panelists\' supervision and guidance, complete independent research reports and briefings. <strong>Recommended preparation:</strong> AME 261, AME 441, AME 481 or equivalents. Genuine interest in design of flight vehicles. Open only to Senior, Master, and Doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2349,'Viterbi','AME','507',3,'Mechanics of Solids I','Analysis of stress and strain; constitutive equations for elastic materials; plane stress and strain; torsion; introduction to plates and shells; energy methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2350,'Viterbi','AME','513',3,'Principles of Combustion','Thermochemistry, equilibrium, chemical kinetics, flame temperature, flame velocity, flame stability, diffusion flames spray combustion, detonation. Equations of motion including reaction, heat transfer, and diffusion.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2351,'Viterbi','AME','515',3,'Advanced Problems in Heat Conduction','Review of analytical methods in heat conduction; moving boundaries melting and freezing; sources and sinks, anisotropic and composite media; numerical methods for steady and unsteady problems. <strong>Recommended preparation:</strong> AME 331, AME 526.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2352,'Viterbi','AME','521',3,'Engineering Vibrations II','Multi-degree of freedom systems; modal analysis. Rayleigh\'s quotient. Continuous systems; modal analysis. Beams, rods,membranes. Colocations, Gelerkin, Rayleigh Ritz methods; finite elements.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2353,'Viterbi','AME','522',3,'Nonlinear Dynamical Systems, Vibrations, and Chaos','Lagrange equations; nonlinear maps and differential equations; fixed points; periodic motion; qualitative/quantitative and local/global analysis; higher order systems; stability; bifurcations; chaos; fractals.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2354,'Viterbi','AME','524',3,'Advanced Engineering Dynamics','Principle of virtual work, constraints, Lagrange\'s equations, Gibbs-Appell equations, Guass\'s principle, theory of rotations, dynamics of rigid bodies, Hamiltonian mechanics, Hamilton-Jacobi equation. <strong>Recommended preparation:</strong> AME 521, AME 525.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2355,'Viterbi','AME','525',3,'Engineering Analysis','Typical engineering problems discussed on a physical basis. Vector analysis; functions of complex variables, infinite series, residues.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2356,'Viterbi','AME','526',3,'Engineering Analytical Methods','Typical engineering problems discussed on a physical basis. Fourier series; Fourier integrals; Laplace transform; partial differential equations; Bessel function.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2357,'Viterbi','AME','527',3,'Elements of Vehicle and Energy Systems Design','Design synthesis of aero/hydro/mechanical systems; techniques of design; conceptual thinking; problem definition, configurational development, analytic engineering approximation, oral briefings and group problem solving. Graduate standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2358,'Viterbi','AME','528a',3,'Finite Element Analysis','Basic concepts; stiffness method; variational methods; displacement method; isoparametric formulation; plane stress and strain; plates and shells; dynamics; stability; nonlinear analysis, heat transfer; computer applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2359,'Viterbi','AME','530a',3,'Dynamics of Incompressible Fluids','A unified discussion of low-speed fluid mechanics including exact solutions; approximation techniques for low and high Reynolds numbers; inviscid flows; surface waves; dynamic stability; turbulence.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2360,'Viterbi','AME','535a',3,'Introduction to Computational Fluid Mechanics','Convergence, consistency, stability: finite difference, finite element, and spectral methods; direct and iterative procedures for steady problems; linear diffusion and advection problems; nonlinear advection problems. <strong>Recommended preparation:</strong> AME 526.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2361,'Viterbi','AME','541',3,'Linear Control Systems II','State space representation, linearization, solution of state equations; controllability and observability; state feedback, state observers; optimal control; output feedback.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2362,'Viterbi','AME','547',3,'Foundations for Manufacturing Automation','Fundamentals of contemporary manufacturing automation; underlying building blocks of the automation system; mechanics-based models for designing automation system; and physical and decision-making aspects of automation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2363,'Viterbi','AME','549',3,'Systems Architecting','Introduction to systems architecture in aerospace, electrical, computer, and manufacturing systems emphasizing the conceptual and acceptance phases and using heuristics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2364,'Viterbi','AME','550',1,'Seminar in Aerospace and Mechanical Engineering','Recent developments and research in aerospace and mechanical engineering and related fields. Oral and written reports.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2365,'Viterbi','AME','551',3,'Mechanical Behavior of Engineering Materials','Mechanical properties of materials; macroscopic mechanical behavior related to structure and microstructure of the material; elementary dislocation theory related to basic strengthening mechanisms; fatigue and fracture; nanomaterials. <strong>Recommended preparation:</strong> MASC 310.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2366,'Viterbi','AME','578',3,'Modern Alternative Energy Conversion Devices','Alternative energy/power conversion including fuel cells, photovoltaic, batteries, and biologically inspired energy processes; biomass conversion and utilization; environmental implications of alternative energy processes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2367,'Viterbi','AME','588',3,'Materials Selection','Materials selection in relationship to design and fabrication, economic considerations, methodology of selection, performance parameter; case studies.',1);
