INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2380,'Viterbi','ASTE','301a',3,'Thermal and Statistical Systems','Thermodynamics and statistical mechanics. Kinetics of atoms, molecules, and photons. Compressible fluid dynamics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2381,'Viterbi','ASTE','330',3,'Introduction to Spacecraft Systems and the Space Environment','Spacecraft systems: attitude determination and control, power, thermal, command and data handling, telecommunication, structures and mechanisms, propulsion. Space environment: atmosphere, gravity gradients, radiation. Prerequisites: ASTE 280 and PHYS 153.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2382,'Viterbi','ASTE','470',3,'Spacecraft Propulsion','Introduction to rocket engineering. Space missions and thrust requirements. Compressible gas dynamics. Propellant chemistry and thermodynamics. Liquid-and-solid-fueled rockets. Nuclear and electric propulsion. Senior or graduate standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2383,'Viterbi','ASTE','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2384,'Viterbi','ASTE','491',1,'Team Projects II','Participation in ASTE undergraduate student team projects. Intended for students with prior project experience. Departmental approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2385,'Viterbi','ASTE','505a',3,'Plasma Dynamics','Charged particle dynamics. Kinetic and fluid theories. Rarefied and collisional plasma flows. Plasma-surface interactions. Waves, instabilities, turbulence. Applications in engineering and space technology. <strong>Recommended preparation:</strong> Graduate standing in engineering or physics. (Duplicates credit in EE-572a)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2386,'Viterbi','ASTE','520',3,'Spacecraft System Design','System components; vehicle structure, propulsion systems, flight dynamics, thermal control, power systems, telecommunication. Interfaces and tradeoffs between these components. Testing, system reliability, and integration.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2387,'Viterbi','ASTE','524',3,'Human Spaceflight','Engineering, technologies, and systems for human spaceflight. Life support, space environments, crew accommodations. Mission operations, safety. Astrodynamics, launch and space vehicles, space stations, planetary bases.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2388,'Viterbi','ASTE','527',3,'Space Exploration Architectures Concept Synthesis Studio','Programmatic/conceptual design synthesis/choice creation methods for complex space missions. Aerospace system engineering/architecting tools to create innovative projects. Evaluated by faculty/industry/NASA experts. Graduate standing in engineering or science. <strong>Recommended preparation:</strong> ASTE-520 or experience in space industry',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2389,'Viterbi','ASTE','535',3,'Space Environments and Spacecraft Interactions','Space environments and interactions with space systems. Vacuum, neutral and ionized species, plasma, radiation, micrometeoroids. Phenomena important for spacecraft operations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2390,'Viterbi','ASTE','546',3,'Computational Plasma Dynamics','Plasma simulation techniques: particle-in-cell (PIC); PIC with Monte Carlo; computational electromagnetics; computational magnetohydrodynamics. Parallelization. Applications in engineering and space plasma physics. Prerequisite: ASTE 505a.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2391,'Viterbi','ASTE','553',3,'Systems for Remote Sensing from Space','The operations and applications of instruments which produce images of ground scene or probe the atmosphere from space. Graduate standing in engineering or physics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2392,'Viterbi','ASTE','556',3,'Spacecraft Structural Dynamics','Applied analytical methods (vibrations of single and multi degree of freedom systems, finite element modeling, spacecraft applications); requirements definition process; analytical cycles; and design verfication. Graduate standing in engineering or science.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2393,'Viterbi','ASTE','577',3,'Entry and Landing Systems for Planetary Surface Exploration','Multi-disciplinary engineering theory, simulation techniques, and unique technologies for landing robotic and human space vehicles on the surface of planets and moons.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2394,'Viterbi','ASTE','580',3,'Orbital Mechanics I','Physical principles; two-body and central force motion; trajectory correction maneuvers; position and velocity in conic orbits; Lambert\'s problem; celestial mechanics; orbital perturbations.',1);
