INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2410,'Viterbi','BME','101',4,'Introduction to Biomedical Engineering','Historical development and survey of major areas comprising biomedical engineering: theoretical neurobiology and systems physiology, biomedical instrumentation, artificial organ and prosthetic devices, biomedical computer applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2411,'Viterbi','BME','308',3,'Computer-Aided Design for Bio-Mechanical Systems','Concepts of computer-aided design in 2-dimensions and 3-dimensions. Creating advanced parts using extrusions, surfaces, and equation driven sketches. Forming assemblies, and sub-assemblies, for motion analysis',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2412,'Viterbi','BME','402',4,'Control and Communication in the Nervous System','An introduction to the structural and functional elements common to nervous systems, with emphasis on cellular dynamics, interneuronal communication, sensory and effector systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2413,'Viterbi','BME','403L',4,'Physiological Systems','A thorough bioengineering treatment of the physiological properties of various mammalian organ systems: e.g., cardiovascular, respiratory, renal, and musculoskeletal.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2414,'Viterbi','BME','404',3,'Biomechanics','Mechanical properties of biological tissues and fluid transport in physiological systems: blood rheology; bi- viscoelastic solids and fluids; gas flow and mixing; prosthesis design.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2415,'Viterbi','BME','405L',4,'Senior Projects: Measurements and Instrumentation','Application of instrumentation and measurement techniques to biomedical engineering projects involving measurement, replacement or augmentation of biomedical systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2416,'Viterbi','BME','406',3,'Introduction to Bioengineering in Medicine','Bioengineering concepts and technologies applied to cancer diagnosis, drug discovery, immunotherapeutic development, and mechanistic research.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2417,'Viterbi','BME','413',4,'Bioengineering Signals and Systems','Introduction to concepts relating to linear signals and systems theory, time and frequency domain analysis, and application of these concepts to problems in Biomedical Engineering.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2418,'Viterbi','BME','423',4,'Statistical Methods in Biomedical Engineering','Applications of parametric and nonparametric tests, analysis of variance, linear regression, time-series analysis, and autoregressive modeling, with biomedical applications to statistical analysis of biomedical data.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2419,'Viterbi','BME','425',3,'Basics of Biomedical Imaging','Engineering, Clinical applications and Modern Physics concepts underlying X-ray Imaging, Computed Tomography (CT), Nuclear Medicine, Positron Emission Tomography, Magnetic Resonance Imaging (MRI), Ultrasound Imaging.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2420,'Viterbi','BME','430',3,'Principles and Applications of Systems Biology','Computational methods for simulation and analysis of metabolic networks, cellular signaling pathways, and gene regulatory networks. Biochemical kinetics, differential equations, stability analysis, sensitivity analysis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2421,'Viterbi','BME','451',3,'Fundamentals of Biomedical Microdevices','Introduction to biomedical devices with emphasis on microtechnologies and biomedical microelectromechanical systems (bioMEMS). Principles for measurement of small-scale biological phenomena and clinical applications. <strong>Recommended preparation:</strong> basic biology and electronics',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2422,'Viterbi','BME','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
