INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2424,'Viterbi','BME','502',4,'Advanced Studies of the Nervous System','Advanced topics on the structure and function of the nervous system examined from the viewpoint of computational systems science. Duplicates credit in BME 512.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2425,'Viterbi','BME','504',3,'Neuromuscular Systems','Introduces the fundamentals of mathematical, Newtonian, and robotic analysis applicable to multi-muscle biomechanical systems. Combines physiology with numerical simulations to understand and predict motor function. <strong>Recommended preparation:</strong> Matlab programming, fundamentals of mechanics, linear algebra.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2426,'Viterbi','BME','505aL',4,'Laboratory Projects in Biomedical Engineering','Integration of biomedical science, engineering principles and state-of-the-art technology for the study of selected physiological systems in the laboratory setting. Laboratory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2427,'Viterbi','BME','511',3,'Physiological Control Systems','Application of control theory to physiological systems; static analysis of closed-loop systems; time-domain analysis of linear control system identification methods; nonlinear control. <strong>Recommended preparation:</strong> BME 513.',1);
