INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2569,'Viterbi','CSCI','495',2,'Senior Project','An original project will be constructed applying computer technology (in either hardware or software) to produce a result useful in the physics classroom or laboratory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2570,'Viterbi','CSCI','499',2,'Special Topics','Selected topics in computer science.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2571,'Viterbi','CSCI','505a',3,'Applied Probability','Populations, permutations, combinations, random variables, distribution and density functions, conditional probability and expectation,; binomial, poisson, and normal distributions; laws of large numbers, central limit theorem. Prerequisite: Consent of Instructor.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2572,'Viterbi','CSCI','510',4,'Software Management and Economics','Theories of management and their application to software projects. Economic analysis of software products and processes. Software cost and schedule estimation, planning and control.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2573,'Viterbi','CSCI','522',4,'Game Engine Development','The principles of developing game engines targeted at modern PC and game console hardware.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2574,'Viterbi','CSCI','526',4,'Advanced Mobile Devices and Game Consoles','Explore the complex engineering process required to design and build a real-time graphics engine to support physical realism on mobile devices. <strong>Recommended preparation:</strong> CSCI 420 or CSCI 580 or an equivalent course in graphics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2575,'Viterbi','CSCI','529a',4,'Advanced Game Projects','Team projects intended to address the multifaceted technical and creative challenges that are inherent to comprehensive game development. <strong>Recommended preparation:</strong> CSCI 522 or CTIN 488.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2576,'Viterbi','CSCI','530',4,'Security Systems','Protecting computer networks and systems using cryptography, authentication, authorization, intrusion detection and response. Includes lab to provide practical experience working with such systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2577,'Viterbi','CSCI','534',4,'Affective Computing','Overview of the theory of human emotion, techniques for recognizing and synthesizing emotional behavior, and design application.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2578,'Viterbi','CSCI','536',3,'Linear Programming and Extensions','Linear programming models for resource allocation; simplex and revised simplex methods; duality; sensitivity; transportation problems; selected extensions to large scale, multiobjective, and special structured models.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2579,'Viterbi','CSCI','537',4,'Foundations of Data Management','Function and design of modern storage systems, including cloud; data management techniques; data modeling; network attached storage, clusters and data centers; relational databases; the map-reduce paradigm.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2580,'Viterbi','CSCI','538',4,'Augmented, Virtual and Mixed Reality','Technical design and implementation of immersive environments; visual simulations, interactive 3D graphics and games.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2581,'Viterbi','CSCI','541',4,'Foundations and Applications of Data Mining','Data mining and machine learning algorithms for analyzing very large data sets. Emphasis on Map Reduce. Case studies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2582,'Viterbi','CSCI','544',4,'Applied Natural Language Processing','Introduction to key components of human language technologies, including: information extraction, sentiment analysis, question answering, machine translation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2583,'Viterbi','CSCI','550',4,'Advanced Data Stores','Selected topics on highly available, elastic data stores. Topics include non-relational data models, simple interfaces and query languages, weak consistency and benchmarking techniques. Prerequisite: CSCI 485 or CSCI 585.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2584,'Viterbi','CSCI','555L',4,'Advanced Operating Systems','Advanced topics in operating system research: new OS structures, novel memory management, communication, file system, process management, reliability and security techniques.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2585,'Viterbi','CSCI','557',4,'Computer Systems Architecture','Computer architecture from a design perspective: Pipelined processors, speculative execution, VLIW, vector processors, GPU/GPGPU, memory technology and systems, interconnection networks, shared-memory and message-passing multiprocessors, chip multiprocessors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2586,'Viterbi','CSCI','560',4,'Information Visualization','Graphical depictions of data for communication, analysis, and decision support. Cognitive processing and perception of visual data and visualizations. Designing effective visualizations. Implementing interactive visualizations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2587,'Viterbi','CSCI','561',4,'Foundations of Artificial Intelligence','Foundations of symbolic intelligent systems, search, logic, knowledge representation, planning, learning.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2588,'Viterbi','CSCI','563',4,'Building Knowledge Graphs','Foundations, techniques, and algorithms for building knowledge graphs and doing so at scale. Topics include information extraction, data alignment, entity linking, and the Semantic Web.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2589,'Viterbi','CSCI','567',4,'Machine Learning','Statistical methods for building intelligent and adaptive systems that improve performance from experiences; Focus on theoretical understanding of these methods and their computational implications. <strong>Recommended preparation:</strong> Undergraduate level training or coursework in linear algebra, multivariate calculus, basic probability and statistics; an undergraduate level course in Artificial Intelligence may be helpful but is not required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2590,'Viterbi','CSCI','570',4,'Analysis of Algorithms','Explores fundamental techniques such as recursion, Fourier transform ordering, dynamic programming for efficient algorithm construction. Examples include arithmetic, algebraic, graph, pattern matching, sorting, searching algorithms.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2591,'Viterbi','CSCI','571',4,'Web Technologies','Advanced study of programming languages with application to the Web. Languages for client-side and server-side processing. Examples taken from: HTML, Java, JavaScript, Perl, XML and others. <strong>Recommended preparation:</strong> knowledge of at least two programming languages',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2592,'Viterbi','CSCI','572',4,'Information Retrieval and Web Search Engines','Examines key aspects of information retrieval as they apply to search engines; web crawling, indexing, querying and quality of results are studied.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2593,'Viterbi','CSCI','576',4,'Multimedia Systems Design','End-to-end multimedia systems - content creation, compression, distribution using modern standards, DRM solutions, Digital-Cinema pipeline, multimedia classification, virtual-augmented reality, natural-language multimedia queries, multimodal media analysis, stereoscopic-holographic technologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2594,'Viterbi','CSCI','577a',4,'Software Engineering','A: Software life-cycle processes; planning considerations for product definition, development, test, implemetation, maintenance. Team project. Graduate standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2595,'Viterbi','CSCI','580',4,'3-D Graphics and Rendering','Course outlines the process of creating images from 3D models. Includes transformations, shading, lighting, rastorization, texturing, and other topics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2596,'Viterbi','CSCI','585',4,'Database Systems','Database system architecture; conceptual database models; semantic, object-oriented, logic-based, and relational databases; user and program interfaces; database system implementation; integrity, security, concurrency and recovery. <strong>Recommended preparation:</strong> Knowledge of relational databases, SQL, relational algebra and physical database design is required. Open only to graduate students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2597,'Viterbi','CSCI','586',4,'Database Systems Interoperability','Federated and multi-database systems, database networking, conceptual and schematic diversity, information sharing and exchange, knowledge discovery, performance issues.',1);
