INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2630,'Viterbi','EE','354L',4,'Introduction to Digital Circuits','Digital system design and implementation; synchronous design of datapath and control; schematic/Verilog-based design, simulation, and implementation in Field Programmable Gate Arrays; timing analysis; semester-end project. Prerequisite: EE 101 or EE 154L. (Duplicates credit in EE 254 and in former EE 201L)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2631,'Viterbi','EE','364',4,'Introduction to Probability and Statistics for Electrical Engineering and Computer Science','Introduction to concepts of randomness and uncertainty: probability, random variables, statistics. Applications to digital communications, signal processing, automatic control, computer engineering and computer science.',1);
