INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2641,'Viterbi','EE','475',3,'Wireless Communication Technology','Fundamentals of wireless communication from a device point of view. Lab experiments and design project.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2642,'Viterbi','EE','477L',4,'MOS VLSI Circuit Design','Analysis and design of digital MOS VLSI circuits including area, delay and power minimization. Laboratory assignments including design, layout, extraction, simulation and automatic synthesis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2643,'Viterbi','EE','479',4,'Analog Integrated Circuit Design','MOSFET and BJT operation and models; elementary amplifier configurations; biasing and references; frequency response; feedback; operational amplifiers. Prerequisite: EE 348.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2644,'Viterbi','EE','482',3,'Linear Control Systems','Analysis of linear control systems; continuous and sampled-data systems, various stability criteria; frequency response and rootlocus compensation techniques. (Duplicates credit in AME 451)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2645,'Viterbi','EE','483',3,'Introduction to Digital Signal Processing','Fundamentals of Digital Signal Processing covering: discrete timelinear systems, quantization, sampling, Z-transforms, Fouriertransforms, FFTs and filter design.',1);
