INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2648,'Viterbi','EE','500',4,'Neural Learning and Computational Intelligence','Computational intelligence techniques that include neural systems, deep learning, adaptive pattern classification, fuzzy function approximation, simulated annealing and evolutionary computing, and hybrid systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2649,'Viterbi','EE','502',3,'Advanced Solid State','Semiconductors, dielectrics and metals, thermoelectric effects, magnetism, magnetic resonance and superconductivity. Prereq: 501',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2650,'Viterbi','EE','503',4,'Probability for Electrical and Computer Engineers','Rigorous coverage of probability, discrete and continuous random variables, functions of multiple random variables, covariance, correlation, random sequences, Markov chains, estimation, and introduction to statistics. Duplicates credit in former EE 464 and EE 465.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2651,'Viterbi','EE','504L',3,'Solid State Processing and Integrated Circuits Laboratory','Laboratory oriented with lectures keyed to practical procedures and processes. Solid-state fabrication and analysis fundamentals; basic device construction techniques. Prerequisite: BSEE',1);
