INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2654,'Viterbi','EE','510',4,'Linear Algebra for Engineering','Introduction to linear algebra and matrix theory and their underlying concepts; applications to engineering problems; mathematically rigorous and foundational to other classes in communication, control, and signal processing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2655,'Viterbi','EE','511',1,'Simulation Methods for Stochastic Systems','Project-oriented investigation of simulation methods used for the analysis and design of complex stochastic systems whose operation and performance are affected by random events. Corequisite: EE 503. <strong>Recommended preparation:</strong> MatLab programming experience.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2656,'Viterbi','EE','512',3,'Stochastic Processes','Probability theory and stochastic processes, including renewal theory, Markov chains, Brownian motion, martingales, and stochastic calculus. Applications in communication networks, queuing theory, and financial systems.',1);
