INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2730,'Viterbi','ISE','105',2,'Introduction to Industrial and Systems Engineering','A combination of plant tours, laboratory experiences, and lecture are used to introduce the philosophy, subject matter, aims, goals, and techniques of industrial and systems engineering.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2731,'Viterbi','ISE','220',3,'Probability Concepts in Engineering','Techniques for handling uncertainties in engineering design: discrete and continuous random variables; expectations, probability distributions and transformations of random variables; limit theorems; approximations and applications. Prerequisite: MATH 126.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2732,'Viterbi','ISE','225',3,'Engineering Statistics I','Sampling distributions; parameter estimation, hypothesis testing; analysis of variance; regression; nonparametric statistics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2733,'Viterbi','ISE','232L',3,'Manufacturing Processes','Basic manufacturing processes including casting, machining, forming and welding; current trends in manufacturing processes including polymer, ceramic and composite material processing, and electronic device fabrication; introduction to numerical control and computer integrated manufacturing. <strong>Recommended preparation:</strong> MASC 110 or CHEM 105aL or CHEM 115aL',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2734,'Viterbi','ISE','315L',3,'Engineering Project Management','Techniques for planning, budgeting, scheduling, obtaining resources, monitoring and control of projects in a corporate environment. Use of software to evaluate performance and assess risks. Prerequisite: ISE 225 and ISE 330.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2735,'Viterbi','ISE','327',3,'Six Sigma and Lean Operations','Standardized methodology for problem solving, basic and advanced statistical tools for root cause identification and solution confirmation, key principles for quality management and organizational excellence. Prerequisite: ISE 225.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2736,'Viterbi','ISE','330',3,'Introduction to Operations Research: Deterministic Models','Introduction to linear programming; transportation and assignment problems; dynamic programming; integer programming; nonliner programming.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2737,'Viterbi','ISE','331',3,'Introduction to Operations Research: Stochastic Models','Stochastic processes; Markov chains; queueing theory and queueing decision models; probabilistic inventory models. <strong>Recommended preparation:</strong> ISE 330',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2738,'Viterbi','ISE','335L',3,'Supply Chain Design','Design of Supply Chains. Product, distribution, transportation and site selection. Analysis and optimization of supply chain networks.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2739,'Viterbi','ISE','370L',4,'Human Factors in Work Design','Physiological systems and psychological characteristics; ergonomics; anthropometry; effects of the physical environment on humans; occupational safety and health; work methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2740,'Viterbi','ISE','382',4,'Database Systems: Concepts, Design and Implementation','Data models for industry applications. Modeling and designing robust databases. Implementing and querying databases with SQL. Innovations in database applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2741,'Viterbi','ISE','410',3,'Planning and Scheduling','Production planning, forecasting, scheduling, and inventory; computer integrated decision systems in analysis and control of production systems. Prerequisite: ISE 330.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2742,'Viterbi','ISE','422L',3,'Configuring Enterprise Resource Planning Systems','Business process integration is the core advantage of using ERP systems. Analyze, configure, and test business processes for a company from the ground up.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2743,'Viterbi','ISE','426',3,'Statistical Quality Control','Quantitative aspects of statistical quality control (process control, acceptance sampling by attribute and by variable, rectifying inspection), quality assurance and the management of QC/QA functions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2744,'Viterbi','ISE','435',3,'Discrete Systems Simulation','Model design to simulate discrete event systems with basic input and output analysis using high order languages, applied to industrial systems analysis and design problems. Prerequisites: CSCI 101 and ISE 225.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2745,'Viterbi','ISE','440',3,'Work, Technology, and Organization','Impact of technology on work and organizational design; effects of automation; design of improvement programs; information infrastructures; teams; individual behavioral outcomes. Upper division standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2746,'Viterbi','ISE','460',3,'Engineering Economy','Utilizing principles of economic analysis for choice of engineering alternatives and engineering systems. Pre-tax and after-tax economy studies. Upper division standing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2747,'Viterbi','ISE','482',3,'Engineering Database Applications','Planning and implementation of engineering information systems that interface with a large database. Emphasis is placed on web-based data entry and retrieval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2748,'Viterbi','ISE','488',3,'Managing Supply Chains with Advanced Planning &amp; Optimization','Drivers and obstacles to the process of coordinating the flow of material/information along the logistics chain. Optimize the supply network, from raw materials to sales. Not available for major credit in engineering except toward undergraduate and graduate programs offered by the Epstein Department of Industrial and Systems Engineering.',1);
