INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2756,'Viterbi','ISE','511L',3,'Mechatronic Systems Engineering','Use of mechanical, electrical, and computer engineering, math, and computer science to design of high performance and sophisticated products and processes and systems involving mechatronic. <strong>Recommended preparation:</strong> Bachelor\'s degree in engineering or physical sciences, and preliminary knowledge of programming in C.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2757,'Viterbi','ISE','512',4,'Software Management and Economics','Theories of management and their application to software projects. Economic analysis of software products and processes. Software cost and schedule estimation, planning and control.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2758,'Viterbi','ISE','514',3,'Advanced Production Planning and Scheduling','Advanced concepts in production planning and scheduling including resource allocation, lot sizing, flow shop and job shop scheduling, workforce scheduling and assembly line balancing. <strong>Recommended preparation:</strong> prior knowledge of operations research and probability theory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2759,'Viterbi','ISE','515',3,'Engineering Project Management','Applying industrial and systems engineering skills to problems drawn from industry, while working in teams of 3-4 students. Teach project management skills and provide direct experience in managing and executing a group project.',1);
