INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2782,'Viterbi','ISE','633',3,'Large Scale Optimization and Machine Learning','Large scale optimization algorithms that arise in modern data science and machine learning applications. Stochastic Optimization, Accelerated Methods, Parallelization, Online Optimization, Randomized Linear Algebra',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2783,'Viterbi','ISE','651',1,'Seminar in Industrial &amp; Systems Engineering','Current research, guest speakers in the field; review papers; guidance in preparing research proposals and special projects. Duplicates credit in the former ISE-650abc. Open only to Fifth Year Seniors and Masters Students.',1);
