INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2785,'Viterbi','ISE','671',4,'Randomized Algorithms','Standard techniques in the design and analysis of randomized algorithms and random structures. Topics include tail bounds, Markov Chains, VC-dimension, probabilistic method. Prerequisite: CSCI 570 or CSCI 670. <strong>Recommended preparation:</strong> Basic background in probability and linear algebra.',1);
