INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2794,'Viterbi','INF','519',4,'Foundations and Policy for Information Security','Threats to information systems; technical and procedural approaches to threat mitigation; policy specification and foundations of policy for secure systems; mechanisms for building secure security services; risk management. <strong>Recommended preparation:</strong> Background in computer security preferred. Recommended previous courses of study include computer science, electrical engineering, computer engineering, management information systems, and/or mathematics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2795,'Viterbi','INF','523',4,'Computer Systems Assurance','Assurance that an information system will behave as expected; assurance approaches for fielding secure information systems; case studies. <strong>Recommended preparation:</strong> Prior degree in computer science, electrical engineering, computer engineering, management information systems, and/or mathematics. Some background in computer security preferred.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2796,'Viterbi','INF','549',4,'Introduction to Computational Thinking and Data Science','Introduction to data analysis techniques and associated computing concepts for non-programmers. Topics include foundations for data analysis, visualization, parallel processing, metadata, provenance, and data stewardship. <strong>Recommended preparation:</strong> Mathematics and logic undergraduate courses.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2797,'Viterbi','INF','550',4,'Overview of Data Informatics in Large Data Environments','Fundamentals of big data informatics techniques. Data lifecycle; the data scientist; machine learning; data mining; NoSQL databases; tools for storage/processing/analytics of large data set on clusters; in-data techniques. <strong>Recommended preparation:</strong> A basic understanding of engineering principles and programming language is desirable.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2798,'Viterbi','INF','551',4,'Foundations of Data Management','Function and design of modern storage systems, including cloud; data management techniques; data modeling; network attached storage, clusters and data centers; relational databases; the map-reduce paradigm.',1);
