INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2836,'Viterbi','ITP','350',4,'Technologies and Practices for Analytics','Overview of the process of data analysis. Reporting, visualization and prediction. Practical applications of analytics through case studies.',1);
