INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2840,'Viterbi','ITP','368',3,'Programming Graphical User Interfaces','Programming applications with dynamic graphical user interfaces. Topics include events, controls, resources, data bindings, styles, and user experience.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2841,'Viterbi','ITP','370',3,'Information Security Management','Management techniques for security projects. Security team management. Laws and regulations for information security. Disaster recovery. Security Audits.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2842,'Viterbi','ITP','375',3,'Digital Forensics','Forensic science techniques. Digital evidence preservation and presentation. Processes and methodologies for digital analysis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2843,'Viterbi','ITP','380',4,'Video Game Programming','Underlying concepts and principles required for programming video games (topics include vectors, transformations, 3-D math, geometric primitives, matrices). Prerequisite: CSCI-104 or ITP-365.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2844,'Viterbi','ITP','383',4,'Database Systems: Concepts, Design and Implementation','Data models for industry applications. Modeling and designing robust databases. Implementing and querying databases with SQL. Innovations in database applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2845,'Viterbi','ITP','391a',4,'Video Game Project Management','Project management basics for the video game industry, focusing on external development. Examine the role of the producer, managing development and coordinating with various stakeholders.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2846,'Viterbi','ITP','404',3,'Modern Technologies of Web Development','Provide students with the necessary skills to build web applications using modern techniques, frameworks, libraries, and tools that are used among developers within the industry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2847,'Viterbi','ITP','410',2,'Social Media for Scientists and Engineers','An introduction to the various ways in which social media and science are intersecting in a compelling manner; new technologies and online communities are powerful tools that are changing the ways in which scientists are engaging one another (inreach) and general audiences (outreach).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2848,'Viterbi','ITP','411',3,'Multimedia and Video Production','Visual communication and storytelling are essential skills, especially in the digital age. Overview of techniques and software to create, edit, and deliver compelling images and video.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2849,'Viterbi','ITP','422L',3,'Configuring Enterprise Resource Planning Systems','Business process integration is the core advantage of using ERP systems. Analyze, configure, and test business processes for a company from the ground up.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2850,'Viterbi','ITP','435',3,'Professional C++','Applications of advanced concepts in C++ including lambda expressions, template metaprogramming, secure coding, parallel programming techniques, and the boost library.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2851,'Viterbi','ITP','445',3,'Macintosh, OSX, and iOS Forensics','Digital Forensics. Digital Evidence. Apple. Mac. Macintosh. OSX. iOS. iPhone. iPad.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2852,'Viterbi','ITP','449',4,'Data Mining: Descriptive and Predictive','Examine and compare practical methods in data mining - data preparation, classification, clustering. Knowledge discovery process is to find patterns and regularities from large datasets.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (2853,'Viterbi','ITP','466',4,'Building the High Tech Startup','Teach students the basic technologies and processes involved in the building web and mobile startups. Students will be introduced to the different aspects of building a web startup including Online Business models, Product management, Agile development processes, Technology platforms and Operations, customer development and online marketing.',1);
