INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3145,'Keck','MEDS','280',2,'The History of Medicine: a Doctor\'s Perspective','Explores the role of medicine/surgery in society, whose ideas about health and disease have undergone enormous changes from antiquity to the present day.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3146,'Keck','MEDS','315',2,'Human Anatomy, Physiology, and the Technology of Medicine','Introduction to human anatomy and physiology in relation to common disease processes, and an examination of how technology has transformed western medicine throughout history.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3147,'Keck','MEDS','320',4,'Human Cadaveric Anatomy','Anatomical structure and function of the major regions of the human body as related to health and disease, using clinical cases, lectures and cadaveric demonstrations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3148,'Keck','MEDS','330',2,'Bionics: Solutions to Enable the Disabled','Focused examination of medical bionics with emphasis on solutions that enable the disabled and detailed discussions and evaluations of the social and technological aspects of bionic medical devices.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3149,'Keck','MEDS','335',2,'Human Development: From Stem to Sternum','An introduction to transformation of a single cell into a mature organism. Prerequisites: BISC-220L or BISC-221L. <strong>Recommended preparation:</strong> BISC-320L.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3150,'Keck','MEDS','350',2,'Neurochemistry of Addiction: Drugs, Brain, and Behavior','The neurochemistry of psychiatric illness and addiction, introduction to brain and nervous system biochemistry, anatomy and disease states. Covers prescribed medications and drugs of abuse.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3151,'Keck','MEDS','420',2,'Clinical Pediatrics','Explores enduring and fascinating topics in Pediatrics through a modern lens, incorporating first hand patient-care experiences in clinics and hospitals, and didactic case-based discussions. Prerequisites: BISC 220L or BISC 221L, and MEDS 220.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3152,'Keck','MEDS','425',2,'Medical Examiner-Coroner: Investigating Death','Emphasizes the team-based approach used by forensic scientists, medical examiners and coroners to investigate the circumstances and determine the cause/manner of death. Prerequisites: BISC 220 or BISC 221 and MEDS 220. Open only to sophomores, juniors, and seniors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3153,'Keck','MEDS','445',2,'Cancer: Introduction to Oncology in the Modern Era','Introduction to the spectrum of clinical and scientific issues surrounding contemporary cancer caremolecular diagnostics, genomic medicine, modern therapeutics, and community/patient engagement. Prerequisites: BISC 220 or BISC 221 and MEDS 220. Open only to juniors and seniors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3154,'Keck','MEDS','460',2,'Emergency Health Care','Principles of emergency medical services, ranging from pre-hospital care and advanced life-saving, to minor injuries and illnesses. Interactive experience with ER physicians/emergency health care providers. Prerequisites: BISC 220 or BISC 221 and MEDS 220. <strong>Recommended preparation:</strong> MEDS 260.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3155,'Keck','MEDS','490',1,'Directed Research in Biomedical Science','Individual research and readings. <strong>Recommended preparation:</strong> MEDS 300 and MEDS 360. Not available for graduate credit.Prerequisite: BISC 220 or BISC 221) and CHEM 103 or CHEM 105a or CHEM115a.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3156,'Keck','MEDS','500',4,'Basic Concepts in Global Health','Exploration of global health issues facing resource-poor societies; emphasizes contributing factors including behavioral and physiological bases, economic, social, and political context.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3157,'Keck','MEDS','503L',4,'Core Principles System I','Introduction to basic medical concepts and biological system functions; examination of core principles of science and medical treatment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3158,'Keck','MEDS','504L',4,'Core Principles System II','Continuation of concepts from MEDS 503L: basic medical concepts and biological system functions; examination of core principles of science and medical treatment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3159,'Keck','MEDS','510',2,'Global Health Modules, Malaria','Critical issues in international control and treatment of malaria. Exploration of biological and epidemiological fundamentals of human-parasite interaction including dynamics of transmission among populations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3160,'Keck','MEDS','514',2,'Global Health Modules, Tropical Disease','Examination of prevalent tropical diseases: epidemiology, clinical manifestations, treatment, impact on economies of tropical countries. Key issues related to these diseases; World Health Organization responses.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3161,'Keck','MEDS','516',2,'Cultural Competence in Health and Medicine','Practical approach to the development of professional skills for providing culturally sensitive clinical health services to ethnically and linguistically diverse patients.',1);
