INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3163,'Keck','MEDS','530c',4,'Foundation of Medicine: Anatomy, Physiology, and Pathology','Continues material from MEDS-530a and MEDS-530b, covering human anatomy (gross anatomy, histology, radiographic anatomy), physiology (cellular physiology, organ systems) and pathology (general, systemic, cellular pathology).',1);
