INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3228,'Keck','PHBI','561',4,'Molecular Biology','Biochemistry and molecular biology of replication, transcription, RNA processing, translation, and regulation of gene expression with emphasis on multicellular eukaryotic organisms and comparisons to prokaryotes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3229,'Keck','PHBI','572',4,'Systems Physiology and Disease I','Mammalian organ systems operation during health, and pathophysiologic analysis of related diseases with focus on muscle, respiratory, cardiovascular and renal systems. Faculty from basic and clinical sciences. Open to graduate students in biomedical science only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3230,'Keck','PHBI','660',2,'Understanding Diseases of Ion Transport','Examination of the genetics, biochemical mechanisms, and physiological characteristics of diseases caused by inherited mutations in ion channels and ion transport proteins.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3231,'Keck','PM','500',4,'Foundations of Health Behavior','Overview of behavioral theory and research in disease prevention and health promotion and in adaptation to chronic disease, including an introduction to measures of outcomes. Prerequisite: Admission to HBR Ph.D. Program or permission of instructor.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3232,'Keck','PM','501',4,'Foundations in Health Education and Promotion','Overview and application of behavioral theories to the field of health education and promotion. Examines the determinants of health behavior and strategies for change at the individual, group and community level.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3233,'Keck','PM','502',4,'Foundations of Public Health','Overview of public health concepts and issues, including population health; ethics; health disparities, determinants, and systems of care; global health; policy; health promotion; environmental health.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3234,'Keck','PM','508',4,'Health Service Delivery in the U.S.','Historical development of the American health care system; determinants of health care utilization; role of health care providers; health policy; public health services; and health care finance.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3235,'Keck','PM','509',4,'Comparative Health Care Systems','Macro-level analysis of the structure and delivery of health care services around the world, including an examination and comparison of health system performance.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3236,'Keck','PM','510L',4,'Principles of Biostatistics','Concepts of biostatistics; appropriate uses and common misuses of health statistics; practice in the application of statistical procedures; introduction to statistical software including EXCEL, SPSS, nQuery. Laboratory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3237,'Keck','PM','511aL',4,'Data Analysis','Major parametric and non-parametric statistical tools used in biomedical research, computer packages including SAS. Includes laboratory. Lecture, 2 hours; laboratory, 2 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3238,'Keck','PM','511bL',4,'Data Analysis','Statistical methods for analysis of categorical data including dichotomous, ordinal, multinomial and count data, using Stata package. Includes laboratory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3239,'Keck','PM','511cL',4,'Data Analysis','Methods and applications for modeling longitudinal, time-to-event and multi- level data. Includes laboratory using R package. Lecture 2 hrs; laboratory 2 hrs. Prerequisites: PM-511b.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3240,'Keck','PM','512',4,'Principles of Epidemiology','Terminology/uses of epidemiology and demography; sources/uses of population data; types of epidemiologic studies; risk assessment; common sources of bias in population studies; principles of screening. <strong>Recommended preparation:</strong> algebra',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3241,'Keck','PM','517a',3,'Research Methods in Epidemiology','Study design, ascertainment of study objects, questionnaire development, various methodological issues in data analysis and interpretation including bias, measurement error, confounding and effect modification.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3242,'Keck','PM','522a',3,'Introduction to the Theory of Statistics','a: Density distribution and hazard functions; normal, chi-square, student\'s t and F distributions; and sampling procedures for single factor and multiple factor designs, distributions. <strong>Recommended preparation:</strong> working knowledge of multivariable calculus and familiarity with linear algebra.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3243,'Keck','PM','525',4,'Culture and Health: Global Perspectives','International variations in health status with a focus on the impact of socioeconomic status, politics, environment, education and gender in etiology of illness, access to health care, progression of disease, and recovery.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3244,'Keck','PM','529',4,'Environmental Health: An Epidemiological Approach','An overview of environmental health, identifying issues in assessing effects of exposure on health and potential interventions for reducing adverse health risks. Prerequisite: PM-512',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3245,'Keck','PM','533',3,'Genetic and Molecular Epidemiology','Genetic principles; design and analysis of family studies; introduction to likelihood estimation; segregation and linkage analysis; biomarkers of exposure, susceptibility, and disease; laboratory methods; susceptibility genes; association and linkage disequilibrium.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3246,'Keck','PM','536',4,'Program Evaluation and Research','Overview of concepts, tools, data collection, analysis methods and designs used to evaluate health promotion programs. Examples from substance abuse prevention, family planning and reproductive health programs.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3247,'Keck','PM','537',4,'Chronic Disease Epidemiology','Overview of causative factors and demographic distribution of major chronic diseases in the western world. Epidemiologic concepts, methods and research design as applied to chronic disease prevention will be emphasized. Prerequisite: PM 512.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3248,'Keck','PM','544L',3,'Multivariate Analysis','Exploratory and inferential techniaues for multivariate data, Hotelling\'s T2, multivariate analysis of variance, classification analysis, principle components, cluster analysis, factor analysis. Involves computer use.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3249,'Keck','PM','546',4,'Biological Threats and Terrorism','History of biowarfare and bioterrorism; proper surveillance techniques, capacity building for public health and medical communities, and the importance of effective communications. Methods of preparedness, prevention and response are examined. <strong>Recommended preparation:</strong> PM 512',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3250,'Keck','PM','552',3,'Statistical Methods in Clinical Trials','Stochastic failure process; parametric/non-parametric models for survival analysis; sample size estimation; multivariate regression for binary outcome/censored survival data; multiple failure modes and competing risks.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3251,'Keck','PM','553',4,'Human Exposure Assessment for Public Health','Examination of important routes of exposure to toxic materials; how to measure exposure; strengths and weaknesses of different measurement techniques; design of exposure assessment studies. <strong>Recommended preparation:</strong> PM-510 or one semester of statistics and background in science or engineering for graduate students not in MPH.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3252,'Keck','PM','555',4,'Environmental Health, Policy and Practice','Examination of environmental public health policies/regulations, the role of science in assessment and policy initiatives, barriers to change, and competing interests that influence policy adoption. <strong>Recommended preparation:</strong> PM-529.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3253,'Keck','PM','558',4,'Environmental Epidemiology: Concepts, Methods, and Practice','Examine epidemiologic methods, concepts, and statistical approaches; case-study seminars with structured critiques of current literature on human-environment interactions affecting public health. <strong>Recommended preparation:</strong> PM-510 and PM-512.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3254,'Keck','PM','559',4,'Cancer Epidemiology','Explores the fundamental concepts of cancer epidemiology, including the molecular basis, descriptive epidemiology, and historical and groundbreaking studies that have impacted the field.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3255,'Keck','PM','563',4,'Organizing and Mobilizing Communities for Public Health','Survey of effective community organizing and mobilization efforts in the U.S. and abroad, using participatory, organizational, community empowerment and public-private partnership models.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3256,'Keck','PM','564',4,'Public Health Leadership and Management','Introduction to leadership and management concepts, tools, and practices in the context of domestic and global public health and healthcare delivery settings.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3257,'Keck','PM','565',4,'Introduction to Global Health','Current public health issues and research topics relating to 21st century challenges and threats. Lessons learned and best practices to strengthen public health systems and enhance public health readiness and preparedness.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3258,'Keck','PM','567',4,'Public Health Disaster Management and Response','Definition and chronology of natural and man-made disasters and their effects on the global community. Structure and organization of disaster management systems and the role of humanitarian organizations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3259,'Keck','PM','568',4,'Ethical Issues in Public Health','Ethical principles in the distribution of health resources, conduct of global public health research, and implementation of public health initiatives across different nations, cultures, religions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3260,'Keck','PM','569',3,'Spatial Statistics','An introduction to statistical methods for analyzing and interpreting spatially referenced data topics include: geostatistics, areal data, point pattern data and visualization. Prerequisite: PM 511a. <strong>Recommended preparation:</strong> familiarity with R.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3261,'Keck','PM','570',4,'Applied Statistical Methods in Human Genetics','An introductory course in the statistical methods used in the analysis of human genetic data.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3262,'Keck','PM','572',4,'Systems Physiology and Disease I','Mammalian organ systems operation during health, and pathophysiologic analysis of related diseases with focus on muscle, respiratory, cardiovascular and renal systems. Faculty from basic and clinical sciences. Open to graduate students in biomedical science only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3263,'Keck','PM','575',3,'Statistical Methods in Environmental Epidemiology','Study designs, exposure-time response, longitudinal, spatial, ecologic correlation and mechanistic models, measurement error interactions, measurement error, public policy implications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3264,'Keck','PM','578',4,'Global Health Governance and Diplomacy','Investigates the way health is organized and administered at the global level, emphasizing the role of international diplomacy and law in governing health.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3265,'Keck','PM','580',4,'Foundations of Child Health','Overview of issues related to infant, child, and adolescent health, including special health considerations at different points in the developmental cycle, health care systems and policies, and health disparities.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3266,'Keck','PM','583',4,'Foundations of Early Childhood Mental Health','Overview of major infant and early childhood mental health issues, relating to the status of child mental health and the importance of comprehensive systems of care for children that support resilience and respond to biological and psychsocial mental health risks.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3267,'Keck','PM','586',4,'Reproductive and Perinatal Epidemiology','Introduction to reproductive health, from preconception to the neonatal and early period of human development. Heavy emphasis on the methods and public health implications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3268,'Keck','PM','588',4,'The Practice of Epidemiology','Provides students with hands on experience of the common methods used in conducting epidemiological studies, and designing and obtaining funding for research projects. Prerequisite: PM-512. <strong>Recommended preparation:</strong> PM 527.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3269,'Keck','PM','590',1,'Directed Research','Research leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3270,'Keck','PM','593',4,'Public Health Practicum','Field placement in a community agency, such as a county hospital department or community-based organization. Open to MPH candidates only. Graded CR/NC. <strong>Recommended preparation:</strong> completion of all coursework.',1);
