INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3275,'Keck','PM','596',1,'Practicum in Public Health','Applied practical experience through field placement in federal, state, and/or local public health agencies/organizations, including community-based organizations; research and school-based settings.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3276,'Keck','PM','597',1,'Capstone in Public Health','Provides the culminating, integrative learning experience for students enrolled in the Master of Public Health program.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3277,'Keck','PM','599',2,'Special Topics','Special topics relevant to the study of selected issues and areas of health behavior research or other aspects of preventive medicine.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3278,'Keck','PM','601',4,'Basic Theory and Strategies in Prevention','Psychosocial basis of health-hazardous lifestyle behaviors and preventive strategies. Recommended preparation PM-500, PM 515',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3279,'Keck','PM','603',4,'Structural Equation Modeling','Factor analytic and structural equation modeling approaches to health behavior research -- conceptual, practical and mathematical.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3280,'Keck','PM','605',4,'Systematic Review and Meta-Analysis','Theoretical basis and practical approaches for identifying, summarizing and interpreting current bodies of scholarly information addressing a defined research question.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3281,'Keck','PM','610',1,'Seminar in Biostatistics and Epidemiology','Special topics of current interest to provide background for research in biostatistics and epidemiology. Based largely on student dissertation research. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3282,'Keck','PM','612a',4,'Clinical Translational Research (CTR)','First of three courses in CTR, a discipline that fosters multidirectional integration of basic, patient-oriented and population-based research with long-term goal of improving public health. <strong>Recommended preparation:</strong> PM 510.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3283,'Keck','PM','615',4,'Intervention Research Grant Proposal Development','Process of applying for research grants in health behavior research, including dissertation grants. Covers the structure of NIH and other grantmaking agencies, the grant review process, tailoring ideas to the grantmaker\'s priorities, research design, development of training and mentoring plans, and budgeting.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3284,'Keck','PM','690a',2,'Directed Research in Health Behavior','Independent research at an advanced level on a problem in the field of Health Behavior. Recommended preparation PM 604. Graded CR/NC.',1);
