INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3307,'Keck','SCRM','511',4,'Developmental Biology and Human Embryology','Survey of anatomical, cellular and molecular processes that underlie human development and congenital malformations, with discussion of other species for comparison. Open only to Stem Cell Biology and Regenerative Medicine majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3308,'Keck','SCRM','513',4,'Stem Cells and Regenerative Medicine','A comprehensive investigation of embryonic, fetal and adult stem cells and the application of stem cell biology to treat disease. Open only to Stem Cell Biology and Regenerative Medicine majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3309,'Keck','SCRM','555',1,'Writing About Stem Cell Biology and Regenerative Medicine','Instruction in writing for various audiences on topics related to stem cell biology and regenerative medicine.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3310,'Keck','SCRM','574',1,'Stem Cell and Developmental Biology Seminar Series','Selected topics in Development, Stem Cell, and Regeneration.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3311,'Keck','SCRM','580',2,'SCRM External Speaker Seminar Series','Reading and discussion of recent papers by the SCRM speaker of the week, and attendance at the speaker\'s seminar. Open only to Stem Cell Biology and Regenerative Medicine majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3312,'Keck','SCRM','590',1,'Independent Research','Independent research conducted under the guidance of faculty in the Department of Stem Cell Biology and Regenerative Medicine. Prerequisites: SCRM 511, SCRM 513, SCRM 515, SCRM 522, SCRM 524, SCRM 555, SCRM 580. Open only to Stem Cell Biology and Regenerative Medicine majors. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3313,'Keck','SCRM','620',1,'Current Topics in Stem Cell Biology and Organogenesis','Selected topics on sub-fields within stem cell biology and organogenesis. Includes review of contemporary literature and research. Open only to master and doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3314,'Keck','TRGN','510',4,'Basic Foundations in Translational Biomedical Informatics','Translational biomedical informatics analysis of next-generation sequencing genomic data, fundamentals, foundations, and concepts for applied analysis, human ontology, application molecular tools, basic computing.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3315,'Keck','TRGN','514',4,'Introduction to Human Genomic Analysis Methods','Translational next generation sequencing genomics microarrays, RNA-seq, exome sequencing, annotation, transcriptome, ontology, pipelines, variants, expression, methylation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3316,'Keck','TRGN','520',4,'Translational Biomedical Informatics Capstone Portfolio','Portfolio, translational biomedical informatics, bioinformatics, genomics, application development, web-based application.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3317,'Keck','TRGN','524',4,'Applications of Genomic Technology in Biomedical Research I','Molecular biotechnology, genomics, next-generation sequencing, epigenomics, drug discovery, biomarkers, microrarrays, commercialization, microarray analysis, expression, proteomics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3318,'Keck','TRGN','526',2,'Clinical Bioinformatics in Genomic Testing','Covers basic understandings of clinical bioinformatics methodologies and practices, along with the genomic technologies used for clinical diagnostic purposes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3319,'Keck','TRGN','536',4,'Biotechnology Primer','Examination of classical and novel techniques used to explore and manipulate gene function and implications for the use of biotechnology in areas of global need. <strong>Recommended preparation:</strong> two semesters of introductory biology; two semesters of molecular biology and genetics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3320,'Keck','TRGN','537',4,'Pathway and Target Discovery','Exploration of the process of discovering and developing novel therapeutic treatments.',1);
