INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (3944,'Price','MS','201',2,'Individual Leadership Studies','Students identify successful leadership characteristics through observation of others and self through experiential learning exercises. Students record observed traits (good and bad) in a dimensional leadership journal and discuss observations in small group settings.',1);
